mapper.select:
select id, user_id
from tbl_cart
where user_id = #{userId}

select
    id, cart_id, item_id, item_name, item_option, item_price, item_count
from tbl_cart_item
where cart_id = #{cartId}
and item_name = #{itemName}
and item_option = #{itemOption}
limit 1

mapper.update:
update tbl_cart_item
set item_count = item_count + 1
where id = #{id}
